import React, { useState, useCallback } from 'react';
import { BookSearchFilters } from '../../services/bookService';

// Props interface for SearchBar
interface SearchBarProps {
  onSearch: (query: string, filters: BookSearchFilters) => void;
  initialQuery?: string;
  initialFilters?: BookSearchFilters;
  isLoading?: boolean;
}

/**
 * SearchBar component for searching books
 * Searches only when submit button is clicked or Enter key is pressed
 */
const SearchBar: React.FC<SearchBarProps> = ({
  onSearch,
  initialQuery = '',
  initialFilters = {},
  isLoading = false
}) => {
  // Search state
  const [query, setQuery] = useState(initialQuery);
  const [filters, setFilters] = useState<BookSearchFilters>(initialFilters);
  const [showFilters, setShowFilters] = useState(false);
  
  // Execute search
  const executeSearch = useCallback(() => {
    if (query.trim()) {
      onSearch(query, filters);
    }
  }, [query, filters, onSearch]);
  
  // Handle search form submission
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    executeSearch();
  };
  
  // Handle key press - search on Enter
  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      executeSearch();
    }
  };
  
  // Handle input change
  const handleQueryChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setQuery(e.target.value);
  };
  
  // Handle filter changes
  const handleFilterChange = (e: React.ChangeEvent<HTMLSelectElement | HTMLInputElement>) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  // Clear search
  const handleClear = () => {
    setQuery('');
    setFilters({});
    // Pass empty strings to properly reset the search state in parent component
    onSearch('', {});
  };

return (
  <div className="search-container">
    <form onSubmit={handleSubmit} className="search-form">
      <div className="search-bar">
        <input
          type="text"
          className="search-input"
          placeholder="Search books by title or author..."
          value={query}
          onChange={handleQueryChange}
          onKeyPress={handleKeyPress}
          disabled={isLoading}
        />
        
        {query && (
          <button 
            type="button"
            className="search-clear-button"
            onClick={handleClear}
            disabled={isLoading}
            aria-label="Clear search"
          >
            &times;
          </button>
        )}
        
        <button
          type="submit"
          className="search-submit-button"
          disabled={isLoading}
        >
          Search
        </button>
        
        <button 
          type="button"
          className={`search-filter-button ${showFilters ? 'active' : ''}`}
          onClick={() => setShowFilters(!showFilters)}
          disabled={isLoading}
          aria-label="Toggle filters"
          aria-expanded={showFilters}
        >
          <span>Filter</span>
          <span className="filter-icon">{showFilters ? '▲' : '▼'}</span>
        </button>
      </div>
    </form>

{!isSearching && (
  <div className="sort-container">
    <label htmlFor="sort-select">Sort by:</label>
    <select 
      id="sort-select"
      value={
        sortBy === 'publishedYear' 
          ? sortOrder === 'desc' ? 'newest' : 'oldest'
          : sortBy === 'averageRating'
            ? 'topRated'
            : sortBy === 'reviewCount'
              ? 'mostReviewed'
              : sortBy
      }
      onChange={handleSortChange}
      disabled={loading}
    >
      <option value="title">Title (A-Z)</option>
      <option value="author">Author (A-Z)</option>
      <option value="newest">Publication Date (Newest)</option>
      <option value="oldest">Publication Date (Oldest)</option>
      <option value="topRated">Top Rated</option>
      <option value="mostReviewed">Most Reviewed</option>
    </select>
  </div>
)}

{searchQuery && searchQuery.trim() !== '' && (
  <div className="results-info">
    Found {pagination.totalBooks} 
    {pagination.totalBooks === 1 ? ' book' : ' books'} 
    for "{searchQuery}"
  </div>
)}

