  const filePath = getDataFilePath(fileName);
  try {
    await fs.access(filePath);
  } catch (err) {
    if (err.code === 'ENOENT') {
      await fs.writeFile(filePath, JSON.stringify([]));
    } else {
      throw err;
    }
  }
};

// Initialize all data files
