{"content": "import api from './api';

/**
 * Book API Service
 * Handles all book-related API calls to the backend
 */
const bookService = {
  /**
   * Get paginated list of books with optional sorting
   * @param {number} page - Page number (1-based)
   * @param {number} limit - Number of books per page
   * @param {string} sortBy - Field to sort by (title, author, publishedYear, averageRating)
   * @param {string} sortOrder - Sort order (asc or desc)
   * @returns {Promise<Object>} - Paginated books response
   */
  async getBooks(page = 1, limit = 10, sortBy = 'title', sortOrder = 'asc') {
    try {
      const response = await api.get('/books', {
        params: { page, limit, sortBy, sortOrder }
      });
      return response.data;
    } catch (error) {
      console.error('Error fetching books:', error);
      throw error;
    }
  },

  /**
   * Search for books by title or author with optional filters
   * @param {string} query - Search term
   * @param {Object} filters - Optional filters
   * @param {string} filters.genre - Filter by genre
   * @param {number} filters.yearFrom - Filter by published year (minimum)
   * @param {number} filters.yearTo - Filter by published year (maximum)
   * @param {number} filters.minRating - Filter by minimum rating
   * @returns {Promise<Object>} - Search results
   */
  async searchBooks(query, filters = {}) {
    try {
      const response = await api.get('/books/search', {
        params: { 
          q: query,
          ...filters
        }
      });
      return response.data;
    } catch (error) {
      console.error('Error searching books:', error);
      throw error;
    }
  },

  /**
   * Get a book by its ID
   * @param {string} id - Book ID
   * @param {boolean} includeReviews - Whether to include reviews in the response
   * @returns {Promise<Object>} - Book details
   */
  async getBookById(id, includeReviews = false) {
    try {
      const response = await api.get(`/books/${id}`, {
        params: { includeReviews }
      });
      return response.data;
    } catch (error) {
      console.error(`Error fetching book with ID ${id}:`, error);
      throw error;
    }
  }
};

export default bookService;", "filePath": "d:\app\assignment2app\frontend\services\bookService.ts"}

