{"content": "import React from 'react';
import Link from 'next/link';
import Image from 'next/image';
import { Book } from '../../services/bookService';

// Default placeholder book cover
const PLACEHOLDER_COVER = 'https://via.placeholder.com/150x225?text=No+Cover';

// Props interface for BookCard
interface BookCardProps {
  book: Book;
}

/**
 * BookCard component displays a book in a card format
 * with image, title, author and rating
 */
const BookCard: React.FC<BookCardProps> = ({ book }) => {
  // Limit description to a reasonable preview length
  const descriptionPreview = book.description.length > 100
    ? `${book.description.substring(0, 100)}...`
    : book.description;

  return (
    <div className="book-card">
      <Link href={`/books/${book.id}`}>
        <a className="book-card-link">
          <div className="book-card-image">
            <Image
              src={book.coverImage || PLACEHOLDER_COVER}
              alt={`Cover for ${book.title}`}
              width={150}
              height={225}
              objectFit="cover"
              unoptimized={book.coverImage?.startsWith('http')}
            />
          </div>

          <div className="book-card-content">
            <h3 className="book-title">{book.title}</h3>
            <p className="book-author">by {book.author}</p>

            <div className="book-rating">
              <span className="rating-stars">
                {''.repeat(Math.round(book.averageRating))}
                {''.repeat(5 - Math.round(book.averageRating))}
              </span>
              <span className="rating-value">{book.averageRating.toFixed(1)}</span>
              <span className="review-count">({book.reviewCount} reviews)</span>
            </div>

            <p className="book-description">{descriptionPreview}</p>

            <div className="book-genres">
              {book.genres.slice(0, 3).map(genre => (
                <span key={genre} className="genre-tag">{genre}</span>
              ))}
              {book.genres.length > 3 && (
                <span className="genre-more">+{book.genres.length - 3}</span>
              )}
            </div>
          </div>
        </a>
      </Link>
    </div>
  );
};

export default BookCard;", "filePath": "d:\app\assignment2app\frontend\components\book\BookCard.tsx"}

{"content": "import React from 'react';
import { Book } from '../../services/bookService';
import BookCard from './BookCard';

// Props interface for BookList
interface BookListProps {
  books: Book[];
  isLoading?: boolean;
  error?: string | null;
}

/**
 * BookList component displays a grid of book cards
 * with loading and error states
 */
const BookList: React.FC<BookListProps> = ({ 
  books, 
  isLoading = false, 
  error = null 
}) => {
  // Render loading state
  if (isLoading) {
    return (
      <div className="book-list-loading">
        <div className="spinner"></div>
        <p>Loading books...</p>
      </div>
    );
  }

  // Render error state
  if (error) {
    return (
      <div className="book-list-error">
        <p>Error loading books: {error}</p>
        <button className="retry-button">Retry</button>
      </div>
    );
  }

  // Render empty state
  if (books.length === 0) {
    return (
      <div className="book-list-empty">
        <p>No books found.</p>
      </div>
    );
  }

  // Render book grid
  return (
    <div className="book-list">
      {books.map(book => (
        <BookCard key={book.id} book={book} />
      ))}
    </div>
  );
};

export default BookList;", "filePath": "d:\app\assignment2app\frontend\components\book\BookList.tsx"}

{"content": "import React from 'react';

// Props interface for Pagination
interface PaginationProps {
  currentPage: number;
  totalPages: number;
  onPageChange: (page: number) => void;
}

/**
 * Pagination component for navigating through pages of content
 */
const Pagination: React.FC<PaginationProps> = ({
  currentPage,
  totalPages,
  onPageChange
}) => {
  // Don't render pagination if there's only one page or no pages
  if (totalPages <= 1) {
    return null;
  }

  // Create an array of page numbers to display
  const getPageNumbers = () => {
    const pages = [];

    // Logic to show limited page numbers with ellipsis for large page counts
    if (totalPages <= 7) {
      // Show all page numbers if 7 or fewer
      for (let i = 1; i <= totalPages; i++) {
        pages.push(i);
      }
    } else {
      // Show first page, last page, and pages around current page
      pages.push(1);

      if (currentPage > 3) {
        pages.push('ellipsis-start');
      }

      // Pages around current page
      const start = Math.max(2, currentPage - 1);
      const end = Math.min(totalPages - 1, currentPage + 1);

      for (let i = start; i <= end; i++) {
        pages.push(i);
      }

      if (currentPage < totalPages - 2) {
        pages.push('ellipsis-end');
      }

      pages.push(totalPages);
    }

    return pages;
  };

  const pageNumbers = getPageNumbers();

  return (
    <div className="pagination">
      {/* Previous page button */}
      <button 
        className="pagination-button prev"
        disabled={currentPage === 1}
        onClick={() => onPageChange(currentPage - 1)}
        aria-label="Go to previous page"
      >
        &laquo; Previous
      </button>

      {/* Page number buttons */}
      <div className="pagination-numbers">
        {pageNumbers.map((page, index) => {
          if (page === 'ellipsis-start' || page === 'ellipsis-end') {
            return <span key={`${page}-${index}`} className="pagination-ellipsis">&hellip;</span>;
          }

          return (
            <button
              key={`page-${page}`}
              className={`pagination-number ${currentPage === page ? 'active' : ''}`}
              onClick={() => onPageChange(page as number)}
              aria-label={`Go to page ${page}`}
              aria-current={currentPage === page ? 'page' : undefined}
            >
              {page}
            </button>
          );
        })}
      </div>

      {/* Next page button */}
      <button 
        className="pagination-button next"
        disabled={currentPage === totalPages}
        onClick={() => onPageChange(currentPage + 1)}
        aria-label="Go to next page"
      >
        Next &raquo;
      </button>
    </div>
  );
};

export default Pagination;", "filePath": "d:\app\assignment2app\frontend\components\ui\Pagination.tsx"}

{"content": "import React, { useState, useEffect, useCallback } from 'react';
import { BookSearchFilters } from '../../services/bookService';

// Props interface for SearchBar
interface SearchBarProps {
  onSearch: (query: string, filters: BookSearchFilters) => void;
  initialQuery?: string;
  initialFilters?: BookSearchFilters;
  isLoading?: boolean;
}

/**
 * SearchBar component for searching books
 * Includes debouncing for efficient API calls
 */
const SearchBar: React.FC<SearchBarProps> = ({
  onSearch,
  initialQuery = '',
  initialFilters = {},
  isLoading = false
}) => {
  // Search state
  const [query, setQuery] = useState(initialQuery);
  const [filters, setFilters] = useState<BookSearchFilters>(initialFilters);
  const [showFilters, setShowFilters] = useState(false);

  // Debounce search to avoid excessive API calls
  const debouncedSearch = useCallback(
    (() => {
      let timeout: NodeJS.Timeout;

      return (searchQuery: string, searchFilters: BookSearchFilters) => {
        clearTimeout(timeout);
        timeout = setTimeout(() => {
          if (searchQuery.trim()) {
            onSearch(searchQuery, searchFilters);
          }
        }, 500); // 500ms delay
      };
    })(),
    [onSearch]
  );

  // Trigger search when query or filters change
  useEffect(() => {
    if (query.trim()) {
      debouncedSearch(query, filters);
    }
  }, [query, filters, debouncedSearch]);

  // Handle input change
  const handleQueryChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setQuery(e.target.value);
  };

  // Handle filter changes
  const handleFilterChange = (e: React.ChangeEvent<HTMLSelectElement | HTMLInputElement>) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Clear search
  const handleClear = () => {
    setQuery('');
    setFilters({});
    onSearch('', {});
  };

  return (
    <div className="search-container">
      <div className="search-bar">
        <input
          type="text"
          className="search-input"
          placeholder="Search books by title or author..."
          value={query}
          onChange={handleQueryChange}
          disabled={isLoading}
        />

        {query && (
          <button 
            className="search-clear-button"
            onClick={handleClear}
            disabled={isLoading}
            aria-label="Clear search"
          >
            &times;
          </button>
        )}

        <button 
          className={`search-filter-button ${showFilters ? 'active' : ''}`}
          onClick={() => setShowFilters(!showFilters)}
          disabled={isLoading}
          aria-label="Toggle filters"
          aria-expanded={showFilters}
        >
          <span>Filter</span>
          <span className="filter-icon">{showFilters ? '' : ''}</span>
        </button>
      </div>

      {showFilters && (
        <div className="search-filters">
          <div className="filter-group">
            <label htmlFor="genre">Genre:</label>
            <select 
              id="genre" 
              name="genre" 
              value={filters.genre || ''}
              onChange={handleFilterChange}
              disabled={isLoading}
            >
              <option value="">All Genres</option>
              <option value="Fiction">Fiction</option>
              <option value="Non-Fiction">Non-Fiction</option>
              <option value="Fantasy">Fantasy</option>
              <option value="Science Fiction">Science Fiction</option>
              <option value="Mystery">Mystery</option>
              <option value="Romance">Romance</option>
              <option value="Classic">Classic</option>
              <option value="Young Adult">Young Adult</option>
            </select>
          </div>

          <div className="filter-group">
            <label htmlFor="yearFrom">Year From:</label>
            <input 
              type="number" 
              id="yearFrom" 
              name="yearFrom"
              min="1000"
              max={new Date().getFullYear()}
              value={filters.yearFrom || ''}
              onChange={handleFilterChange}
              disabled={isLoading}
            />
          </div>

          <div className="filter-group">
            <label htmlFor="yearTo">Year To:</label>
            <input 
              type="number" 
              id="yearTo" 
              name="yearTo"
              min="1000"
              max={new Date().getFullYear()}
              value={filters.yearTo || ''}
              onChange={handleFilterChange}
              disabled={isLoading}
            />
          </div>

          <div className="filter-group">
            <label htmlFor="minRating">Min Rating:</label>
            <select 
              id="minRating" 
              name="minRating" 
              value={filters.minRating || ''}
              onChange={handleFilterChange}
              disabled={isLoading}
            >
              <option value="">Any Rating</option>
              <option value="1">1+ Stars</option>
              <option value="2">2+ Stars</option>
              <option value="3">3+ Stars</option>
              <option value="4">4+ Stars</option>
              <option value="4.5">4.5+ Stars</option>
            </select>
          </div>

          <button 
            className="filter-reset-button"
            onClick={() => setFilters({})}
            disabled={isLoading}
          >
            Reset Filters
          </button>
        </div>
      )}
    </div>
  );
};

