{"content": "import React, { useState, useEffect } from 'react';
import Head from 'next/head';
import { useRouter } from 'next/router';
import BookList from '../../components/book/BookList';
import SearchBar from '../../components/ui/SearchBar';
import Pagination from '../../components/ui/Pagination';
import bookService, { Book, BookSearchFilters } from '../../services/bookService';

// Interface for API response pagination data
interface PaginationData {
  page: number;
  limit: number;
  totalPages: number;
  totalBooks: number;
}

/**
 * Books Index Page - Displays a paginated list of books with search functionality
 */
const BooksIndexPage: React.FC = () => {
  // State for books data
  const [books, setBooks] = useState<Book[]>([]);
  const [pagination, setPagination] = useState<PaginationData>({
    page: 1,
    limit: 10,
    totalPages: 1,
    totalBooks: 0
  });

  // State for sorting
  const [sortBy, setSortBy] = useState<string>('title');
  const [sortOrder, setSortOrder] = useState<string>('asc');

  // State for search and loading
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [searchFilters, setSearchFilters] = useState<BookSearchFilters>({});
  const [isSearching, setIsSearching] = useState<boolean>(false);

  // Loading and error states
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  const router = useRouter();

  // Load books on initial render and when page, sort, or other params change
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        setLoading(true);
        setError(null);

        if (searchQuery) {
          // Search mode - fetch filtered books
          const response = await bookService.searchBooks(searchQuery, searchFilters);
          setBooks(response.books);
          setPagination({
            ...pagination,
            totalBooks: response.count,
            totalPages: Math.ceil(response.count / pagination.limit) || 1
          });
          setIsSearching(true);
        } else {
          // Regular browse mode - fetch paginated books
          const response = await bookService.getBooks(
            pagination.page, 
            pagination.limit, 
            sortBy, 
            sortOrder
          );
          setBooks(response.books);
          setPagination({
            page: response.page,
            limit: response.limit,
            totalPages: response.totalPages,
            totalBooks: response.totalBooks
          });
          setIsSearching(false);
        }
      } catch (err) {
        console.error('Error fetching books:', err);
        setError('Failed to load books. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, [
    pagination.page, 
    pagination.limit, 
    sortBy, 
    sortOrder, 
    searchQuery, 
    searchFilters
  ]);

  // Handle page change
  const handlePageChange = (newPage: number) => {
    setPagination({
      ...pagination,
      page: newPage
    });

    // Scroll to top of the page
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Handle search
  const handleSearch = (query: string, filters: BookSearchFilters) => {
    setSearchQuery(query);
    setSearchFilters(filters);
    // Reset to first page when searching
    setPagination({
      ...pagination,
      page: 1
    });
  };

  // Handle sort change
  const handleSortChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const value = e.target.value;
    if (value === 'title' || value === 'author') {
      setSortBy(value);
      setSortOrder('asc');
    } else if (value === 'newest') {
      setSortBy('publishedYear');
      setSortOrder('desc');
    } else if (value === 'oldest') {
      setSortBy('publishedYear');
      setSortOrder('asc');
    } else if (value === 'topRated') {
      setSortBy('averageRating');
      setSortOrder('desc');
    } else if (value === 'mostReviewed') {
      setSortBy('reviewCount');
      setSortOrder('desc');
    }
  };

  return (
    <div className="books-page">
      <Head>
        <title>{searchQuery ? `Search: ${searchQuery}` : 'Browse Books'} - BookReview</title>
        <meta name="description" content="Browse and search for books on BookReview Platform" />
      </Head>

      <div className="books-page-header">
        <h1 className="page-title">{searchQuery ? 'Search Results' : 'Browse Books'}</h1>

        <div className="books-search-container">
          <SearchBar
            onSearch={handleSearch}
            initialQuery={searchQuery}
            initialFilters={searchFilters}
            isLoading={loading}
          />
        </div>

        <div className="books-controls">
          {!isSearching && (
            <div className="sort-container">
              <label htmlFor="sort-select">Sort by:</label>
              <select 
                id="sort-select"
                value={
                  sortBy === 'publishedYear' 
                    ? sortOrder === 'desc' ? 'newest' : 'oldest'
                    : sortBy === 'averageRating'
                      ? 'topRated'
                      : sortBy === 'reviewCount'
                        ? 'mostReviewed'
                        : sortBy
                }
                onChange={handleSortChange}
                disabled={loading}
              >
                <option value="title">Title (A-Z)</option>
                <option value="author">Author (A-Z)</option>
                <option value="newest">Publication Date (Newest)</option>
                <option value="oldest">Publication Date (Oldest)</option>
                <option value="topRated">Top Rated</option>
                <option value="mostReviewed">Most Reviewed</option>
              </select>
            </div>
          )}

          {searchQuery && (
            <div className="results-info">
              Found {pagination.totalBooks} 
              {pagination.totalBooks === 1 ? ' book' : ' books'} 
              for "{searchQuery}"
            </div>
          )}
        </div>
      </div>

      <div className="books-page-content">
        <BookList 
          books={books} 
          isLoading={loading} 
          error={error} 
        />

        {!loading && !error && books.length > 0 && (
          <div className="pagination-container">
            <Pagination
              currentPage={pagination.page}
              totalPages={pagination.totalPages}
              onPageChange={handlePageChange}
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default BooksIndexPage;", "filePath": "d:\app\assignment2app\frontend\pages\books\index.tsx"}

{"content": "import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import Head from 'next/head';
import Image from 'next/image';
import Link from 'next/link';
import bookService, { Book } from '../../services/bookService';

// Default placeholder book cover
const PLACEHOLDER_COVER = 'https://via.placeholder.com/300x450?text=No+Cover';

/**
 * Book Details Page - Displays detailed information about a specific book
 */
const BookDetailsPage: React.FC = () => {
  // State for book data
  const [book, setBook] = useState<Book | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Get book ID from router
  const router = useRouter();
  const { id } = router.query;

  // Fetch book details when ID is available
  useEffect(() => {
    // Only fetch if we have an ID and it's a string
    if (id && typeof id === 'string') {
      const fetchBookDetails = async () => {
        try {
          setLoading(true);
          setError(null);

          const response = await bookService.getBookById(id, true);
          setBook(response.book);
        } catch (err: any) {
          console.error('Error fetching book details:', err);

          // Handle 404 specifically
          if (err.response && err.response.status === 404) {
            setError('Book not found. It may have been removed or the URL is incorrect.');
          } else {
            setError('Failed to load book details. Please try again.');
          }
        } finally {
          setLoading(false);
        }
      };

      fetchBookDetails();
    }
  }, [id]);

  // Render loading state
  if (loading) {
    return (
      <div className="book-details-loading">
        <div className="spinner"></div>
        <p>Loading book details...</p>
      </div>
    );
  }

  // Render error state
  if (error) {
    return (
      <div className="book-details-error">
        <h2>Error</h2>
        <p>{error}</p>
        <Link href="/books">
          <a className="back-button">Back to Books</a>
        </Link>
      </div>
    );
  }

  // Render not found state
  if (!book) {
    return (
      <div className="book-details-not-found">
        <h2>Book Not Found</h2>
        <p>We couldn't find the book you're looking for.</p>
        <Link href="/books">
          <a className="back-button">Back to Books</a>
        </Link>
      </div>
    );
  }

  return (
    <div className="book-details-page">
      <Head>
        <title>{book.title} by {book.author} - BookReview</title>
        <meta name="description" content={`Read about ${book.title} by ${book.author} and see reviews on BookReview Platform`} />
      </Head>

      <div className="book-details-header">
        <Link href="/books">
          <a className="back-button">&larr; Back to Books</a>
        </Link>
      </div>

      <div className="book-details-container">
        <div className="book-details-cover">
          <Image
            src={book.coverImage || PLACEHOLDER_COVER}
            alt={`Cover for ${book.title}`}
            width={300}
            height={450}
            objectFit="cover"
            unoptimized={book.coverImage?.startsWith('http')}
          />
        </div>

        <div className="book-details-info">
          <h1 className="book-title">{book.title}</h1>
          <h2 className="book-author">by {book.author}</h2>

          <div className="book-rating">
            <span className="rating-stars">
              {''.repeat(Math.round(book.averageRating))}
              {''.repeat(5 - Math.round(book.averageRating))}
            </span>
            <span className="rating-value">{book.averageRating.toFixed(1)}</span>
            <span className="review-count">({book.reviewCount} reviews)</span>
          </div>

          <div className="book-metadata">
            <div className="metadata-item">
              <span className="metadata-label">Published:</span>
              <span className="metadata-value">{book.publishedYear}</span>
            </div>

            <div className="metadata-item">
              <span className="metadata-label">Genres:</span>
              <div className="book-genres">
                {book.genres.map(genre => (
                  <span key={genre} className="genre-tag">{genre}</span>
                ))}
              </div>
            </div>
          </div>

          <div className="book-description">
            <h3>Description</h3>
            <p>{book.description}</p>
          </div>

          <div className="book-details-actions">
            <button className="action-button primary-button">
              Write a Review
            </button>
            <button className="action-button secondary-button">
              Add to Favorites
            </button>
          </div>
        </div>
      </div>

      <div className="book-reviews">
        <h3 className="reviews-header">Reviews</h3>
        <div className="reviews-placeholder">
          <p>Reviews will be implemented in User Story 2.2</p>
        </div>
      </div>
    </div>
  );
};

export default BookDetailsPage;", "filePath": "d:\app\assignment2app\frontend\pages\books\[id].tsx"}

{"content": "/* Book Cards */
.book-card {
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  overflow: hidden;
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  background-color: white;
  height: 100%;
}

.book-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

.book-card-link {
  display: flex;
  flex-direction: column;
  height: 100%;
  color: inherit;
  text-decoration: none;
}

.book-card-image {
  text-align: center;
  background: #f5f5f5;
  padding: 20px 0;
  min-height: 225px;
}

.book-card-content {
  padding: 15px;
  display: flex;
  flex-direction: column;
  flex-grow: 1;
}

.book-title {
  font-size: 1.2rem;
  margin-bottom: 5px;
  font-weight: 600;
}

.book-author {
  font-size: 0.95rem;
  color: #666;
  margin-top: 0;
  margin-bottom: 10px;
}

.book-rating {
  display: flex;
  align-items: center;
  margin-bottom: 10px;
}

.rating-stars {
  color: #f8ce0b;
  letter-spacing: 2px;
  margin-right: 8px;
}

.rating-value {
  font-weight: bold;
  margin-right: 5px;
}

.review-count {
  color: #666;
  font-size: 0.85rem;
}

.book-description {
  font-size: 0.9rem;
  line-height: 1.4;
  margin-bottom: 15px;
  flex-grow: 1;
}

.book-genres {
  display: flex;
  flex-wrap: wrap;
  gap: 5px;
}

.genre-tag {
  background: #f0f0f0;
  padding: 3px 8px;
  border-radius: 12px;
  font-size: 0.8rem;
  color: #333;
}

.genre-more {
  font-size: 0.8rem;
  color: #666;
  padding: 3px 8px;
}

/* Book List */
.book-list {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 25px;
  margin-top: 20px;
}

.book-list-loading, .book-list-error, .book-list-empty {
  text-align: center;
  padding: 50px 0;
}

.spinner {
  border: 4px solid rgba(0, 0, 0, 0.1);
  border-left: 4px solid #3498db;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  animation: spin 1s linear infinite;
  margin: 0 auto 20px;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.retry-button {
  background: #3498db;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 15px;
}

/* Pagination */
.pagination {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 40px 0;
  gap: 10px;
}

.pagination-button {
  background: #f0f0f0;
  border: 1px solid #ddd;
  padding: 8px 15px;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.2s;
}

.pagination-button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.pagination-button:not(:disabled):hover {
  background: #e0e0e0;
}

.pagination-numbers {
  display: flex;
  gap: 5px;
}

.pagination-number {
  width: 35px;
  height: 35px;
  display: flex;
  align-items: center;
  justify-content: center;
  border: 1px solid #ddd;
  border-radius: 4px;
  background: transparent;
  cursor: pointer;
}

.pagination-number.active {
  background: #3498db;
  color: white;
  border-color: #3498db;
}

.pagination-ellipsis {
  display: flex;
  align-items: center;
  padding: 0 5px;
}

/* Search */
.search-container {
  width: 100%;
  max-width: 800px;
  margin: 0 auto 20px;
}

.search-bar {
  display: flex;
  position: relative;
}

.search-input {
  flex-grow: 1;
  padding: 12px 15px;
  border: 1px solid #ddd;
  border-radius: 4px 0 0 4px;
  font-size: 1rem;
}

.search-clear-button {
  position: absolute;
