  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  font-size: 1.2rem;
  color: #999;
  cursor: pointer;
}

.search-filter-button {
  padding: 0 15px;
  background: #f0f0f0;
  border: 1px solid #ddd;
  border-left: none;
  border-radius: 0 4px 4px 0;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 5px;
}

.search-filter-button.active {
  background: #e0e0e0;
}

.search-filters {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 15px;
  padding: 15px;
  background: #f9f9f9;
  border: 1px solid #ddd;
  border-top: none;
  border-radius: 0 0 4px 4px;
}

.filter-group {
  display: flex;
  flex-direction: column;
  gap: 5px;
}

.filter-group label {
  font-size: 0.9rem;
  font-weight: 500;
}

.filter-group select, .filter-group input {
  padding: 8px;
  border: 1px solid #ddd;
  border-radius: 4px;
}

.filter-reset-button {
  background: #f0f0f0;
  border: 1px solid #ddd;
  padding: 8px 15px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: auto;
}

/* Books Page */
.books-page {
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
}

.books-page-header {
  margin-bottom: 30px;
}

.page-title {
  margin-bottom: 20px;
}

.books-controls {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 20px;
}

.sort-container {
  display: flex;
  align-items: center;
  gap: 10px;
}

.sort-container select {
  padding: 8px;
  border: 1px solid #ddd;
  border-radius: 4px;
}

.results-info {
  font-style: italic;
  color: #666;
}

/* Book Details Page */
.book-details-page {
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
}

.book-details-header {
  margin-bottom: 30px;
}

.back-button {
  display: inline-block;
  margin-bottom: 20px;
  color: #3498db;
  text-decoration: none;
}

.book-details-container {
  display: grid;
  grid-template-columns: 300px 1fr;
  gap: 30px;
}

.book-details-cover {
  text-align: center;
}

.book-details-info {
  display: flex;
  flex-direction: column;
  gap: 15px;
}

.book-details-info .book-title {
  font-size: 2rem;
  margin-bottom: 0;
}

.book-details-info .book-author {
  font-size: 1.3rem;
  margin-top: 5px;
}

.book-details-info .book-rating {
  margin: 15px 0;
}

.book-metadata {
  border-top: 1px solid #eee;
  border-bottom: 1px solid #eee;
  padding: 15px 0;
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
}

.metadata-item {
  display: flex;
  align-items: center;
  gap: 10px;
}

.metadata-label {
  font-weight: 500;
}

.book-description {
  margin-top: 20px;
}

.book-description h3 {
  margin-bottom: 10px;
}

.book-description p {
  line-height: 1.6;
}

.book-details-actions {
  display: flex;
  gap: 15px;
  margin-top: 20px;
}

.action-button {
  padding: 10px 20px;
  border-radius: 4px;
  border: none;
  cursor: pointer;
  font-weight: 500;
}

.primary-button {
  background: #3498db;
  color: white;
}

.secondary-button {
  background: #f0f0f0;
  color: #333;
  border: 1px solid #ddd;
}

.book-reviews {
  margin-top: 50px;
}

.reviews-header {
  font-size: 1.5rem;
  margin-bottom: 20px;
}

.reviews-placeholder {
  padding: 30px;
  text-align: center;
  background: #f9f9f9;
  border-radius: 4px;
}

/* Error States */
.book-details-loading, .book-details-error, .book-details-not-found {
  text-align: center;
  padding: 50px 0;
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .book-details-container {
    grid-template-columns: 1fr;
  }

  .book-details-cover {
    max-width: 250px;
    margin: 0 auto;
  }

  .book-list {
    grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
    gap: 15px;
  }

  .search-filters {
    grid-template-columns: 1fr;
  }

