{"content": "const bookModel = require('../../src/models/book.model');
const fs = require('fs').promises;
const path = require('path');
const { getDataFilePath } = require('../../src/config/db');
const { seedBooks } = require('../../src/data/seedData');

// Mock path for testing
jest.mock('../../src/config/db', () => ({
  getDataFilePath: jest.fn(() => path.join(__dirname, '../mocks/books.test.json'))
}));

describe('Book Model', () => {
  // Setup test data
  beforeAll(async () => {
    // Create test directory if it doesn't exist
    const testDir = path.join(__dirname, '../mocks');
    try {
      await fs.mkdir(testDir, { recursive: true });
    } catch (err) {
      if (err.code !== 'EEXIST') throw err;
    }

    // Seed the test data
    const books = await seedBooks();
    expect(books.length).toBeGreaterThan(0);
  });

  // Clean up after tests
  afterAll(async () => {
    try {
      await fs.unlink(getDataFilePath());
    } catch (err) {
      console.error('Error cleaning up test data:', err);
    }
  });

  describe('findAll', () => {
    test('should return all books', async () => {
      const books = await bookModel.findAll();
      expect(Array.isArray(books)).toBe(true);
      expect(books.length).toBeGreaterThan(0);
    });
  });

  describe('findById', () => {
    test('should return a book when given a valid ID', async () => {
      const books = await bookModel.findAll();
      const testBook = books[0];

      const book = await bookModel.findById(testBook.id);
      expect(book).toBeDefined();
      expect(book.id).toBe(testBook.id);
      expect(book.title).toBe(testBook.title);
    });

    test('should return undefined for invalid ID', async () => {
      const book = await bookModel.findById('invalid-id');
      expect(book).toBeUndefined();
    });
  });

  describe('search', () => {
    test('should find books matching title search', async () => {
      const books = await bookModel.search('harry');
      expect(Array.isArray(books)).toBe(true);
      expect(books.length).toBeGreaterThan(0);
      expect(books[0].title.toLowerCase()).toContain('harry');
    });

    test('should find books matching author search', async () => {
      const books = await bookModel.search('tolkien');
      expect(Array.isArray(books)).toBe(true);
      expect(books.length).toBeGreaterThan(0);
      expect(books[0].author.toLowerCase()).toContain('tolkien');
    });

    test('should return empty array for no matches', async () => {
      const books = await bookModel.search('xyznonexistent');
      expect(Array.isArray(books)).toBe(true);
      expect(books.length).toBe(0);
    });

    test('should filter by genre when provided', async () => {
      const books = await bookModel.search('fiction', { genre: 'Classic' });
      expect(Array.isArray(books)).toBe(true);
      books.forEach(book => {
        expect(book.genres).toContain('Classic');
      });
    });

    test('should filter by year range when provided', async () => {
      const yearFrom = 1950;
      const yearTo = 2000;

      const books = await bookModel.search('fiction', { 
        yearFrom, 
        yearTo 
      });

      expect(Array.isArray(books)).toBe(true);
      books.forEach(book => {
        expect(book.publishedYear).toBeGreaterThanOrEqual(yearFrom);
        expect(book.publishedYear).toBeLessThanOrEqual(yearTo);
      });
    });

    test('should filter by minimum rating when provided', async () => {
      const minRating = 4.5;

      const books = await bookModel.search('fiction', { minRating });

      expect(Array.isArray(books)).toBe(true);
      books.forEach(book => {
        expect(book.averageRating).toBeGreaterThanOrEqual(minRating);
      });
    });
  });

  describe('getPaginated', () => {
    test('should return paginated results', async () => {
      const page = 1;
      const limit = 5;

      const result = await bookModel.getPaginated(page, limit);

      expect(result.books.length).toBeLessThanOrEqual(limit);
      expect(result.page).toBe(page);
      expect(result.limit).toBe(limit);
      expect(result.totalPages).toBeGreaterThanOrEqual(1);
    });

    test('should handle sorting by title ascending', async () => {
      const result = await bookModel.getPaginated(1, 10, 'title', 'asc');

      const titles = result.books.map(book => book.title);
      const sortedTitles = [...titles].sort();

      expect(titles).toEqual(sortedTitles);
    });

    test('should handle sorting by title descending', async () => {
      const result = await bookModel.getPaginated(1, 10, 'title', 'desc');

      const titles = result.books.map(book => book.title);
      const sortedTitles = [...titles].sort().reverse();

      expect(titles).toEqual(sortedTitles);
    });

    test('should handle sorting by rating', async () => {
      const result = await bookModel.getPaginated(1, 10, 'averageRating', 'desc');

      for (let i = 0; i < result.books.length - 1; i++) {
        expect(result.books[i].averageRating).toBeGreaterThanOrEqual(result.books[i + 1].averageRating);
      }
    });
  });

  describe('findByGenre', () => {
    test('should find books by genre', async () => {
      const genre = 'Fantasy';
      const books = await bookModel.findByGenre(genre);

      expect(Array.isArray(books)).toBe(true);
      books.forEach(book => {
        expect(book.genres).toContain(genre);
      });
    });

    test('should return empty array for non-existent genre', async () => {
      const books = await bookModel.findByGenre('NonExistentGenre');
      expect(Array.isArray(books)).toBe(true);
      expect(books.length).toBe(0);
    });
  });
});", "filePath": "d:\app\assignment2app\backend\tests\models\book.model.test.js"}

{"content": "const bookController = require('../../src/controllers/book.controller');
const bookModel = require('../../src/models/book.model');

// Mock book model
jest.mock('../../src/models/book.model');

describe('Book Controller', () => {
  let req;
  let res;
  let next;

  // Set up mocks before each test
  beforeEach(() => {
    req = {
      params: {},
      query: {}
    };
    res = {
      status: jest.fn().mockReturnThis(),
      json: jest.fn()
    };
    next = jest.fn();
  });

  afterEach(() => {
    jest.clearAllMocks();
  });

  describe('getAllBooks', () => {
    test('should get paginated books with default values', async () => {
      // Mock data
      const mockPaginatedResult = {
        books: [{ id: '1', title: 'Test Book' }],
        totalBooks: 1,
        page: 1,
        limit: 10,
        totalPages: 1
      };

      // Setup mock
      bookModel.getPaginated.mockResolvedValue(mockPaginatedResult);

      // Call controller
      await bookController.getAllBooks(req, res, next);

      // Assertions
      expect(bookModel.getPaginated).toHaveBeenCalledWith(1, 10, 'title', 'asc');
      expect(res.status).toHaveBeenCalledWith(200);
      expect(res.json).toHaveBeenCalledWith(expect.objectContaining({
        books: mockPaginatedResult.books,
        links: expect.any(Object)
      }));
    });

    test('should pass query parameters to model', async () => {
      // Mock request
      req.query = {
        page: '2',
        limit: '5',
        sortBy: 'author',
        sortOrder: 'desc'
      };

      // Mock data
      const mockPaginatedResult = {
        books: [{ id: '1', title: 'Test Book' }],
        totalBooks: 1,
        page: 2,
        limit: 5,
        totalPages: 1
      };

      // Setup mock
      bookModel.getPaginated.mockResolvedValue(mockPaginatedResult);

      // Call controller
      await bookController.getAllBooks(req, res, next);

      // Assertions
      expect(bookModel.getPaginated).toHaveBeenCalledWith(2, 5, 'author', 'desc');
      expect(res.status).toHaveBeenCalledWith(200);
    });

    test('should handle invalid pagination parameters', async () => {
      // Mock invalid request
      req.query = {
        page: '0',
        limit: '0'
      };

      // Call controller
      await bookController.getAllBooks(req, res, next);

      // Assertions
      expect(bookModel.getPaginated).not.toHaveBeenCalled();
      expect(res.status).toHaveBeenCalledWith(400);
      expect(res.json).toHaveBeenCalledWith(expect.objectContaining({
        message: expect.any(String)
      }));
    });

    test('should handle errors', async () => {
      // Setup mock to throw error
      const error = new Error('Database error');
      bookModel.getPaginated.mockRejectedValue(error);

      // Call controller
      await bookController.getAllBooks(req, res, next);

      // Assertions
      expect(next).toHaveBeenCalledWith(error);
    });
  });

  describe('searchBooks', () => {
    test('should search books with query', async () => {
      // Mock request
      req.query = { q: 'test' };

      // Mock data
      const mockBooks = [
        { id: '1', title: 'Test Book 1' },
        { id: '2', title: 'Test Book 2' }
      ];

      // Setup mock
      bookModel.search.mockResolvedValue(mockBooks);

      // Call controller
      await bookController.searchBooks(req, res, next);

      // Assertions
      expect(bookModel.search).toHaveBeenCalledWith('test', expect.any(Object));
      expect(res.status).toHaveBeenCalledWith(200);
      expect(res.json).toHaveBeenCalledWith(expect.objectContaining({
        books: mockBooks,
        count: 2
      }));
    });

    test('should handle empty search query', async () => {
      // Mock request with empty query
      req.query = { q: '' };

      // Call controller
      await bookController.searchBooks(req, res, next);

      // Assertions
      expect(bookModel.search).not.toHaveBeenCalled();
      expect(res.status).toHaveBeenCalledWith(400);
      expect(res.json).toHaveBeenCalledWith(expect.objectContaining({
        message: expect.any(String)
      }));
    });

    test('should apply filters when provided', async () => {
      // Mock request with filters
      req.query = { 
        q: 'test',
        genre: 'Fiction',
        yearFrom: '2000',
        yearTo: '2020',
        minRating: '4'
      };

      // Mock data
      const mockBooks = [{ id: '1', title: 'Test Fiction Book' }];

      // Setup mock
      bookModel.search.mockResolvedValue(mockBooks);

      // Call controller
      await bookController.searchBooks(req, res, next);

      // Assertions
      expect(bookModel.search).toHaveBeenCalledWith('test', {
        genre: 'Fiction',
        yearFrom: 2000,
        yearTo: 2020,
        minRating: 4
      });
    });

    test('should handle errors', async () => {
      // Mock request
      req.query = { q: 'test' };

      // Setup mock to throw error
      const error = new Error('Search error');
      bookModel.search.mockRejectedValue(error);

      // Call controller
      await bookController.searchBooks(req, res, next);

      // Assertions
      expect(next).toHaveBeenCalledWith(error);
    });
  });

  describe('getBookById', () => {
    test('should get book by id', async () => {
      // Mock request
      req.params = { id: '123' };

      // Mock data
      const mockBook = { 
        id: '123', 
        title: 'Test Book',
        author: 'Test Author' 
      };

      // Setup mock
      bookModel.findById.mockResolvedValue(mockBook);

      // Call controller
      await bookController.getBookById(req, res, next);

      // Assertions
      expect(bookModel.findById).toHaveBeenCalledWith('123');
      expect(res.status).toHaveBeenCalledWith(200);
      expect(res.json).toHaveBeenCalledWith(expect.objectContaining({
        book: mockBook
      }));
    });

    test('should handle book not found', async () => {
      // Mock request
      req.params = { id: 'nonexistent' };

      // Setup mock
      bookModel.findById.mockResolvedValue(null);

      // Call controller
      await bookController.getBookById(req, res, next);

      // Assertions
      expect(res.status).toHaveBeenCalledWith(404);
      expect(res.json).toHaveBeenCalledWith(expect.objectContaining({
        message: expect.stringContaining('not found')
      }));
    });

    test('should include reviews when requested', async () => {
      // Mock request
      req.params = { id: '123' };
      req.query = { includeReviews: 'true' };

      // Mock data
      const mockBook = { id: '123', title: 'Test Book' };

      // Setup mock
      bookModel.findById.mockResolvedValue(mockBook);

      // Call controller
      await bookController.getBookById(req, res, next);

      // Assertions
      expect(res.json).toHaveBeenCalledWith(expect.objectContaining({
        book: mockBook,
        reviews: expect.any(Array),
        message: expect.any(String)
      }));
    });

    test('should handle invalid ID', async () => {
      // Mock request with non-string ID
      req.params = { id: null };

      // Call controller
      await bookController.getBookById(req, res, next);

      // Assertions
      expect(bookModel.findById).not.toHaveBeenCalled();
      expect(res.status).toHaveBeenCalledWith(400);
    });

    test('should handle errors', async () => {
      // Mock request
      req.params = { id: '123' };

      // Setup mock to throw error
      const error = new Error('Database error');
      bookModel.findById.mockRejectedValue(error);

      // Call controller
      await bookController.getBookById(req, res, next);

      // Assertions
      expect(next).toHaveBeenCalledWith(error);
    });
  });
});", "filePath": "d:\app\assignment2app\backend\tests\controllers\book.controller.test.js"}

