const BaseModel = require('./base.model');
const userModel = require('./user.model');

class ReviewModel extends BaseModel {
  constructor() {
    super('reviews');
  }

  // Find reviews by book ID with optional pagination and sorting
  async findByBookId(bookId, options = {}) {
    const { page = 1, limit = 10, sortBy = 'timestamp', sortOrder = 'desc' } = options;
    let reviews = await this.findAll();
    
    // Filter by book ID
    reviews = reviews.filter(review => review.bookId === bookId);
    
    // Apply sorting
    reviews = this.sortReviews(reviews, sortBy, sortOrder);
    
    // Apply pagination
    const startIndex = (page - 1) * limit;
    const endIndex = page * limit;
    
    return {
      reviews: reviews.slice(startIndex, endIndex),
      totalReviews: reviews.length,
      page,
      limit,
      totalPages: Math.ceil(reviews.length / limit)
    };
  }

  // Find reviews by book ID with user information
  async findByBookIdWithUserInfo(bookId, options = {}) {
    const result = await this.findByBookId(bookId, options);
    
    // Fetch user information for each review
    const reviewsWithUserInfo = await Promise.all(
      result.reviews.map(async review => {
        const user = await userModel.findById(review.userId);
        return {
          ...review,
          user: user ? { id: user.id, name: user.name } : null
        };
      })
    );
    
    return {
      ...result,
      reviews: reviewsWithUserInfo
    };
  }

  // Find reviews by user ID with optional pagination
  async findByUserId(userId, options = {}) {
    const { page = 1, limit = 10, sortBy = 'timestamp', sortOrder = 'desc' } = options;
    let reviews = await this.findAll();
    
    // Filter by user ID
    reviews = reviews.filter(review => review.userId === userId);
    
    // Apply sorting
    reviews = this.sortReviews(reviews, sortBy, sortOrder);
    
    // Apply pagination
    const startIndex = (page - 1) * limit;
    const endIndex = page * limit;
    
    return {
      reviews: reviews.slice(startIndex, endIndex),
      totalReviews: reviews.length,
      page,
      limit,
      totalPages: Math.ceil(reviews.length / limit)
    };
  }

  // Create a review with timestamp
  async create(data) {
    return super.create({
      ...data,
      timestamp: new Date().toISOString()
    });
  }

  // Check if user has already reviewed a book
  async userHasReviewed(userId, bookId) {
    const reviews = await this.findAll();
    return reviews.some(review => 
      review.userId === userId && review.bookId === bookId
    );
  }

  // Helper method to sort reviews
  sortReviews(reviews, sortBy = 'timestamp', sortOrder = 'desc') {
    const validSortFields = ['timestamp', 'rating'];
    
    // Default to timestamp if sortBy is not valid
    const field = validSortFields.includes(sortBy) ? sortBy : 'timestamp';
    
    return [...reviews].sort((a, b) => {
      // For dates, convert to Date objects for comparison
      if (field === 'timestamp') {
        const dateA = new Date(a[field]);
        const dateB = new Date(b[field]);
        return sortOrder === 'asc' ? dateA - dateB : dateB - dateA;
      }
      
      // For numeric fields
      return sortOrder === 'asc' ? a[field] - b[field] : b[field] - a[field];
    });
  }
}

module.exports = new ReviewModel();

// Get reviews for a book
const getBookReviews = async (req, res, next) => {
  try {
    const bookId = req.params.bookId;
    const { page, limit, sortBy, sortOrder } = req.query;
    
    // Parse pagination and sorting options
    const options = {
      page: page ? parseInt(page) : 1,
      limit: limit ? parseInt(limit) : 10,
      sortBy: sortBy || 'timestamp',
      sortOrder: sortOrder || 'desc'
    };
    
    const result = await reviewModel.findByBookIdWithUserInfo(bookId, options);
    
    res.status(200).json(result);
  } catch (error) {
    next(error);
  }
};

// Create a review for a book
const createReview = async (req, res, next) => {
  try {
    const bookId = req.params.bookId;
    const userId = req.user.id;
    const { text, rating } = req.body;
    
    // Validate required fields
    if (!text || text.trim() === '') {
      return res.status(400).json({ message: 'Review text is required' });
    }
    
    // Validate rating
    if (!rating) {
      return res.status(400).json({ message: 'Rating is required' });
    }
    
    const ratingNum = Number(rating);
    if (isNaN(ratingNum) || ratingNum < 1 || ratingNum > 5) {
      return res.status(400).json({ message: 'Rating must be between 1 and 5' });
    }
    
    // Check if book exists
    const book = await bookModel.findById(bookId);
    if (!book) {
      return res.status(404).json({ message: 'Book not found' });
    }
    
    // Check if user has already reviewed this book
    const hasReviewed = await reviewModel.userHasReviewed(userId, bookId);
    if (hasReviewed) {
      return res.status(409).json({ message: 'You have already reviewed this book' });
    }
    
    // Create review
    const review = await reviewModel.create({
      bookId,
      userId,
      text,
      rating: ratingNum
    });
    
    // Update book rating
    await bookModel.updateBookRatings(bookId);
    
    res.status(201).json({
      message: 'Review created successfully',
      review
    });
  } catch (error) {
    next(error);
  }
};

// Update a review
const updateReview = async (req, res, next) => {
  try {
    const reviewId = req.params.id;
    const userId = req.user.id;
    const { text, rating } = req.body;
    
    // Validate required fields
    if (!text || text.trim() === '') {
      return res.status(400).json({ message: 'Review text is required' });
    }
    
    // Validate rating
    if (!rating) {
      return res.status(400).json({ message: 'Rating is required' });
    }
    
    const ratingNum = Number(rating);
    if (isNaN(ratingNum) || ratingNum < 1 || ratingNum > 5) {
      return res.status(400).json({ message: 'Rating must be between 1 and 5' });
    }
    
    // Find the review
    const review = await reviewModel.findById(reviewId);
    if (!review) {
      return res.status(404).json({ message: 'Review not found' });
    }
    
    // Check if user owns the review
    if (review.userId !== userId) {
      return res.status(403).json({ message: 'You can only update your own reviews' });
    }
    
    // Update review
    const updatedReview = await reviewModel.update(reviewId, {
      text,
      rating: ratingNum
    });
    
    // Update book rating
    await bookModel.updateBookRatings(review.bookId);
    
    res.status(200).json({
      message: 'Review updated successfully',
      review: updatedReview
    });
  } catch (error) {
    next(error);
  }
};

// Delete a review
const deleteReview = async (req, res, next) => {
  try {
    const reviewId = req.params.id;
    const userId = req.user.id;
    
    // Find the review
    const review = await reviewModel.findById(reviewId);
    if (!review) {
      return res.status(404).json({ message: 'Review not found' });
    }
    
    // Check if user owns the review
    if (review.userId !== userId) {
      return res.status(403).json({ message: 'You can only delete your own reviews' });
    }
    
    // Store bookId before deletion to update ratings
    const bookId = review.bookId;
    
    // Delete the review
    const deleted = await reviewModel.delete(reviewId);
    
    if (!deleted) {
      return res.status(500).json({ message: 'Failed to delete review' });
    }
    
    // Update book rating
    await bookModel.updateBookRatings(bookId);
    
    res.status(200).json({
      message: 'Review deleted successfully'
    });
  } catch (error) {
    next(error);
  }
};

// Get reviews by user
const getUserReviews = async (req, res, next) => {
  try {
    const userId = req.params.id;
    const { page, limit, sortBy, sortOrder } = req.query;
    
    // Parse pagination and sorting options
    const options = {
      page: page ? parseInt(page) : 1,
      limit: limit ? parseInt(limit) : 10,
      sortBy: sortBy || 'timestamp',
      sortOrder: sortOrder || 'desc'
    };
    
    const result = await reviewModel.findByUserId(userId, options);
    
    res.status(200).json(result);
  } catch (error) {
    next(error);
  }
};

const express = require('express');
const router = express.Router();
const reviewController = require('../controllers/review.controller');
const authMiddleware = require('../middleware/auth.middleware');

// Protected routes
router.use(authMiddleware.authenticate);

// Update and delete reviews
router.put('/:id', reviewController.updateReview);
router.delete('/:id', reviewController.deleteReview);

module.exports = router;

import React, { useState, useEffect } from 'react';
