  }, [id]);

  // Fetch reviews
  useEffect(() => {
    if (!id) return;

    const fetchReviews = async () => {
      try {
        setReviewsLoading(true);
        const reviewsResult = await reviewService.getBookReviews(
          id as string,
          currentPage,
          10,
          sortBy,
          sortOrder
        );
        setReviewsData(reviewsResult);
      } catch (err) {
        console.error('Failed to load reviews:', err);
      } finally {
        setReviewsLoading(false);
      }
    };

    fetchReviews();
  }, [id, currentPage, sortBy, sortOrder]);

  // Handle page change
  const handlePageChange = (page: number) => {
    setCurrentPage(page);
  };

  // Handle sort change
  const handleSortChange = (newSortBy: string, newSortOrder: string) => {
    setSortBy(newSortBy);
    setSortOrder(newSortOrder);
  };

  // Handle review submission
  const handleReviewSubmit = async (reviewData: { bookId: string; text: string; rating: number }) => {
    try {
      setSubmittingReview(true);

      if (reviewToEdit) {
        // Update existing review
        await reviewService.updateReview(reviewToEdit.id, {
          text: reviewData.text,
          rating: reviewData.rating
        });
        setReviewToEdit(null);
      } else {
        // Create new review
        await reviewService.createReview(reviewData.bookId, {
          text: reviewData.text,
          rating: reviewData.rating
        });
      }

      // Refresh reviews and book data (to update rating)
      const reviewsResult = await reviewService.getBookReviews(id as string, currentPage, 10, sortBy, sortOrder);
      setReviewsData(reviewsResult);

      const bookData = await bookService.getBookById(id as string);
      setBook(bookData);

    } catch (err) {
      console.error('Failed to submit review:', err);
    } finally {
      setSubmittingReview(false);
    }
  };

  // Handle review edit
  const handleEditReview = (review: Review) => {
    setReviewToEdit(review);
  };

  // Handle review delete modal
  const handleDeleteClick = (reviewId: string) => {
    setReviewToDelete(reviewId);
    setIsDeleteModalOpen(true);
  };

  // Handle review deletion
  const handleDeleteReview = async () => {
    if (!reviewToDelete) return;

    try {
      setDeletingReview(true);
      await reviewService.deleteReview(reviewToDelete);

      // Refresh reviews and book data
      const reviewsResult = await reviewService.getBookReviews(id as string, currentPage, 10, sortBy, sortOrder);
      setReviewsData(reviewsResult);

      const bookData = await bookService.getBookById(id as string);
      setBook(bookData);

      setIsDeleteModalOpen(false);
      setReviewToDelete(null);
    } catch (err) {
      console.error('Failed to delete review:', err);
    } finally {
      setDeletingReview(false);
    }
  };

  // Loading state
  if (loading) {
    return <div className="loading">Loading book details...</div>;
  }

  // Error state
  if (error || !book) {
    return (
      <div className="error">
        <p>{error || 'Book not found'}</p>
        <Link href="/books">Back to Books</Link>
      </div>
    );
  }

  return (
    <div className="book-details-page">
      <div className="container">
        <div className="book-details">
          <div className="book-cover">
            {book.coverImage ? (
              <Image 
                src={book.coverImage} 
                alt={`Cover of ${book.title}`} 
                width={300} 
                height={450} 
              />
            ) : (
              <div className="placeholder-cover">No Cover Available</div>
            )}
          </div>

          <div className="book-info">
            <h1>{book.title}</h1>
            <h2>by {book.author}</h2>

            <div className="book-rating">
              <StarRating rating={book.averageRating} readOnly />
              <span className="rating-text">
                {book.averageRating.toFixed(1)} ({book.reviewCount} reviews)
              </span>
            </div>

            <div className="book-metadata">
              <p><strong>Published:</strong> {book.publishedYear}</p>
              <p><strong>Genres:</strong> {book.genres.join(', ')}</p>
            </div>

            <div className="book-description">
              <h3>Description</h3>
              <p>{book.description}</p>
            </div>
          </div>
        </div>

        <div className="book-reviews-section">
          <h2>Reviews</h2>

          {isAuthenticated ? (
            !reviewToEdit && !reviewsData.reviews.some(review => review.userId === user?.id) ? (
              <div className="write-review">
                <ReviewForm
                  bookId={book.id}
                  onSubmit={handleReviewSubmit}
                  isSubmitting={submittingReview}
                />
              </div>
            ) : null
          ) : (
            <div className="login-prompt">
              <p>Please <Link href="/auth/login">log in</Link> to leave a review.</p>
            </div>
          )}

          {reviewToEdit && (
            <div className="edit-review">
              <ReviewForm
                bookId={book.id}
                review={reviewToEdit}
                onSubmit={handleReviewSubmit}
                onCancel={() => setReviewToEdit(null)}
                isSubmitting={submittingReview}
              />
            </div>
          )}

          <ReviewList
            bookId={book.id}
            reviews={reviewsData.reviews}
            totalReviews={reviewsData.totalReviews}
            currentPage={reviewsData.page}
            totalPages={reviewsData.totalPages}
            userId={user?.id}
            onPageChange={handlePageChange}
            onSortChange={handleSortChange}
            onEditReview={handleEditReview}
            onDeleteReview={handleDeleteClick}
            loading={reviewsLoading}
          />
        </div>

        <DeleteReviewModal
          isOpen={isDeleteModalOpen}
          onClose={() => setIsDeleteModalOpen(false)}
          onConfirm={handleDeleteReview}
          isDeleting={deletingReview}
        />
      </div>
    </div>
  );
};

