// Add to review.model.js
async findByUserIdWithBookInfo(userId, options = {}) {
  const reviews = await this.findAll();
  let userReviews = reviews.filter(review => review.userId === userId);

  // Fetch all books for the reviews
  const bookModel = require('./book.model');
  const books = await bookModel.findAll();

  // Enrich reviews with book information
  const enrichedReviews = userReviews.map(review => {
    const book = books.find(book => book.id === review.bookId);
    return {
      ...review,
      book: book ? {
        id: book.id,
        title: book.title,
        author: book.author,
        coverImage: book.coverImage
      } : null
    };
  });

  // Apply sorting
  enrichedReviews.sort((a, b) => {
    const aValue = a[options.sortBy || 'timestamp'];
    const bValue = b[options.sortBy || 'timestamp'];

    if (options.sortOrder === 'asc') {
      return aValue > bValue ? 1 : -1;
    } else {
      return aValue < bValue ? 1 : -1;
    }
  });

  // Apply pagination
  const page = options.page || 1;
  const limit = options.limit || 10;
  const startIndex = (page - 1) * limit;
  const endIndex = page * limit;

  const paginatedReviews = enrichedReviews.slice(startIndex, endIndex);

  return {
    reviews: paginatedReviews,
    totalItems: enrichedReviews.length,
    page,
    limit,
    totalPages: Math.ceil(enrichedReviews.length / limit)
  };
}

// Update getUserReviews in review.controller.js
const getUserReviews = async (req, res, next) => {
  try {
    const userId = req.params.id;
    const { page, limit, sortBy, sortOrder } = req.query;

    // Parse pagination and sorting options
    const options = {
      page: page ? parseInt(page) : 1,
      limit: limit ? parseInt(limit) : 10,
      sortBy: sortBy || 'timestamp',
      sortOrder: sortOrder || 'desc'
    };

    const result = await reviewModel.findByUserIdWithBookInfo(userId, options);

    res.status(200).json(result);
  } catch (error) {
    next(error);
  }
};
