import Link from 'next/link';
import { Book } from '../../types';

interface BookCardProps {
  book: Book;
  showActions?: boolean;
  onRemoveFromFavorites?: () => void;
}

const BookCard: React.FC<BookCardProps> = ({ 
  book, 
  showActions = false,
  onRemoveFromFavorites
}) => {
  return (
    <div className="book-card">
      <Link href={`/books/${book.id}`}>
        <div className="book-card-content">
          <div className="book-cover">
            <img 
              src={book.coverImage || '/images/default-cover.jpg'} 
              alt={book.title}
            />
          </div>
          <div className="book-info">
            <h3 className="book-title">{book.title}</h3>
            <p className="book-author">{book.author}</p>
            <div className="book-rating">
              <span className="stars">
                {''.repeat(Math.round(book.averageRating))}
                {''.repeat(5 - Math.round(book.averageRating))}
              </span>
              <span className="rating-value">
                {book.averageRating.toFixed(1)} ({book.reviewCount} reviews)
              </span>
            </div>
          </div>
        </div>
      </Link>

      {showActions && onRemoveFromFavorites && (
        <div className="book-actions">
          <button 
            className="remove-favorite-button"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              onRemoveFromFavorites();
            }}
          >
            Remove from favorites
          </button>
        </div>
      )}
    </div>
  );
};

