{"content": "import api from './api';

/**
 * Interface for Book object
 */
export interface Book {
  id: string;
  title: string;
  author: string;
  description: string;
  coverImage: string;
  genres: string[];
  publishedYear: number;
  averageRating: number;
  reviewCount: number;
}

/**
 * Interface for search filters
 */
export interface BookSearchFilters {
  genre?: string;
  yearFrom?: number;
  yearTo?: number;
  minRating?: number;
  maxRating?: number;
  hasReviews?: boolean;
}

/**
 * Interface for rating distribution
 */
export interface RatingDistribution {
  [key: string]: number;
  '1': number;
  '2': number;
  '3': number;
  '4': number;
  '5': number;
}

/**
 * Interface for detailed rating information
 */
export interface BookRatingDetails {
  bookId: string;
  title: string;
  averageRating: number;
  reviewCount: number;
  distribution: RatingDistribution;
  positivePercentage: number;
}

/**
 * Book API Service
 * Handles all book-related API calls to the backend
 */
const bookService = {
  /**
   * Get paginated list of books with optional sorting and filtering
   */
  async getBooks(page = 1, limit = 10, sortBy = 'title', sortOrder = 'asc', minRating?: number) {
    try {
      const response = await api.get('/books', {
        params: { 
          page, 
          limit, 
          sortBy, 
          sortOrder,
          minRating
        }
      });
      return response.data;
    } catch (error) {
      console.error('Error fetching books:', error);
      throw error;
    }
  },

  /**
   * Search for books by title or author with optional filters
   */
  async searchBooks(query: string, filters: BookSearchFilters = {}) {
    try {
      const response = await api.get('/books/search', {
        params: { 
          q: query,
          ...filters
        }
      });
      return response.data;
    } catch (error) {
      console.error('Error searching books:', error);
      throw error;
    }
  },

  /**
   * Get a book by its ID
   */
  async getBookById(id: string, includeReviews: boolean = false) {
    try {
      const response = await api.get(`/books/${id}`, {
        params: { includeReviews }
      });
      return response.data;
    } catch (error) {
      console.error(`Error fetching book with ID ${id}:`, error);
      throw error;
    }
  },

  /**
   * Get detailed rating information for a book
   */
  async getBookRatingDetails(id: string): Promise<BookRatingDetails> {
    try {
      const response = await api.get(`/books/${id}/ratings`);
      return response.data;
    } catch (error) {
      console.error(`Error fetching rating details for book ${id}:`, error);
      throw error;
    }
  }
};

export default bookService;", "filePath": "d:\app\assignment2app\frontend\services\bookService.ts"}

