/**
 * Create a new review for a book
 * @param {string} bookId - Book ID to review
 * @param {object} reviewData - Review data (text, rating)
 * @returns {Promise<Object>} - Created review with updated book rating
 */
async createReview(bookId: string, reviewData: { text: string; rating: number }): Promise<{ 
  review: Review; 
  message: string;
  bookRating?: {
    averageRating: number;
    reviewCount: number;
  } 
}> {
  try {
    const response = await api.post(`/books/${bookId}/reviews`, reviewData);
    return response.data;
  } catch (error) {
    console.error('Error creating review:', error);
    throw error;
  }
},

/**
 * Update an existing review
 * @param {string} reviewId - Review ID to update
 * @param {object} reviewData - Updated review data (text, rating)
 * @returns {Promise<Object>} - Updated review with updated book rating
 */
async updateReview(reviewId: string, reviewData: { text: string; rating: number }): Promise<{ 
  review: Review; 
  message: string;
  bookRating?: {
    averageRating: number;
    reviewCount: number;
  } 
}> {
  try {
    const response = await api.put(`/reviews/${reviewId}`, reviewData);
    return response.data;
  } catch (error) {
    console.error('Error updating review:', error);
    throw error;
  }
},

/**
 * Delete a review
 * @param {string} reviewId - Review ID to delete
 * @returns {Promise<Object>} - Success message with updated book rating
 */
async deleteReview(reviewId: string): Promise<{ 
  message: string;
  bookRating?: {
    averageRating: number;
    reviewCount: number;
  } 
}> {
  try {
    const response = await api.delete(`/reviews/${reviewId}`);
    return response.data;
  } catch (error) {
    console.error('Error deleting review:', error);
    throw error;
  }
}

// Handle review submission (create or update)
const handleReviewSubmit = async (reviewData: { bookId: string; text: string; rating: number }) => {
  if (!id || typeof id !== 'string') return;
  
  try {
    setSubmittingReview(true);
    
    if (reviewToEdit) {
      // Update existing review
      const updateResponse = await reviewService.updateReview(reviewToEdit.id, {
        text: reviewData.text,
        rating: reviewData.rating
      });
      
      setReviewToEdit(null);
      
      // Immediately update book rating if available in the response
      if (updateResponse.bookRating && book) {
        setBook({
          ...book,
          averageRating: updateResponse.bookRating.averageRating,
          reviewCount: updateResponse.bookRating.reviewCount
        });
        
        // Also update rating details if they exist
        if (ratingDetails) {
          setRatingDetails({
            ...ratingDetails,
            averageRating: updateResponse.bookRating.averageRating,
            reviewCount: updateResponse.bookRating.reviewCount
          });
        }
      }
    } else {
      // Create new review
      const createResponse = await reviewService.createReview(id, {
        text: reviewData.text,
        rating: reviewData.rating
      });
      
      // Immediately update book rating if available in the response
      if (createResponse.bookRating && book) {
        setBook({
          ...book,
          averageRating: createResponse.bookRating.averageRating,
          reviewCount: createResponse.bookRating.reviewCount
        });
        
        // Also update rating details if they exist
        if (ratingDetails) {
          setRatingDetails({
            ...ratingDetails,
            averageRating: createResponse.bookRating.averageRating,
            reviewCount: createResponse.bookRating.reviewCount
          });
        }
      }
    }
    
    // Refresh reviews
    const reviewsResult = await reviewService.getBookReviews(id, currentPage, 10, sortBy, sortOrder);
    setReviewsData(reviewsResult);
    
  } catch (err) {
    console.error('Failed to submit review:', err);
  } finally {
    setSubmittingReview(false);
  }
};

// Handle review deletion confirmation
const handleDeleteReview = async () => {
  if (!reviewToDelete || !id || typeof id !== 'string') return;
  
  try {
    setDeletingReview(true);
    const deleteResponse = await reviewService.deleteReview(reviewToDelete);
    
    // Immediately update book rating if available in the response
    if (deleteResponse.bookRating && book) {
      setBook({
        ...book,
        averageRating: deleteResponse.bookRating.averageRating,
        reviewCount: deleteResponse.bookRating.reviewCount
      });
      
      // Also update rating details if they exist
      if (ratingDetails) {
        setRatingDetails({
          ...ratingDetails,
          averageRating: deleteResponse.bookRating.averageRating,
          reviewCount: deleteResponse.bookRating.reviewCount
        });
      }
    }
    
    // Refresh reviews
    const reviewsResult = await reviewService.getBookReviews(id, currentPage, 10, sortBy, sortOrder);
    setReviewsData(reviewsResult);
    
    setIsDeleteModalOpen(false);
    setReviewToDelete(null);
  } catch (err) {
    console.error('Failed to delete review:', err);
  } finally {
    setDeletingReview(false);
  }
};

