      const book = await bookModel.findById(testBook.id);

      expect(book).toEqual(testBook);
    });

    it('should return undefined when book does not exist', async () => {
      const book = await bookModel.findById('nonexistent-id');
      expect(book).toBeUndefined();
    });
  });

