    it('should find books by title', async () => {
      // Create test books
      await bookModel.create(bookFactory({ title: 'Harry Potter', author: 'J.K. Rowling' }));
      await bookModel.create(bookFactory({ title: 'The Hobbit', author: 'J.R.R. Tolkien' }));
      await bookModel.create(bookFactory({ title: 'Potter Crafts', author: 'Jane Smith' }));

      const results = await bookModel.searchBooks('potter');

      expect(results).toHaveLength(2);
      expect(results[0].title).toMatch(/Potter/i);
      expect(results[1].title).toMatch(/Potter/i);
    });

    it('should find books by author', async () => {
      // Create test books
      await bookModel.create(bookFactory({ title: 'Book One', author: 'John Smith' }));
      await bookModel.create(bookFactory({ title: 'Book Two', author: 'Jane Smith' }));
      await bookModel.create(bookFactory({ title: 'Book Three', author: 'Jack Johnson' }));

      const results = await bookModel.searchBooks('smith');

      expect(results).toHaveLength(2);
      expect(results[0].author).toMatch(/Smith/i);
      expect(results[1].author).toMatch(/Smith/i);
    });

