const [isFavorite, setIsFavorite] = useState<boolean>(false);
const [favoritesLoading, setFavoritesLoading] = useState<boolean>(false);
const [favoriteProcessing, setFavoriteProcessing] = useState<boolean>(false);

// Handle sort change for reviews
const handleSortChange = (newSortBy: string, newSortOrder: string) => {
  setSortBy(newSortBy);
  setSortOrder(newSortOrder);
};

// Handle toggling favorite status
const handleToggleFavorite = async () => {
  if (!isAuthenticated || favoriteProcessing || !id || typeof id !== 'string') return;
  
  try {
    setFavoriteProcessing(true);
    
    if (isFavorite) {
      await userService.removeFavorite(id);
    } else {
      await userService.addFavorite(id);
    }
    
    // Update local state
    setIsFavorite(!isFavorite);
    
  } catch (error) {
    console.error('Failed to toggle favorite status:', error);
  } finally {
    setFavoriteProcessing(false);
  }
};

import React, { useState, useEffect, useCallback } from 'react';
import { Book } from '../../services/bookService';
import userService from '../../services/userService';
import { useAuth } from '../../store/auth-context';
import BookCard from './BookCard';

  const { isAuthenticated, user } = useAuth();
  const [favorites, setFavorites] = useState<string[]>([]);
  const [favoritesLoaded, setFavoritesLoaded] = useState(false);
  const [refreshFavorites, setRefreshFavorites] = useState(0);

  // Create a memoized fetch favorites function
  const fetchFavorites = useCallback(async () => {
    if (isAuthenticated && user) {
      try {
        setFavoritesLoaded(false);
        const response = await userService.getFavorites(user.id);
        // Extract book IDs from favorites
        const favoriteIds = response.favorites.map((book: Book) => book.id);
        setFavorites(favoriteIds);
      } catch (err) {
        console.error('Failed to fetch favorites:', err);
      } finally {
        setFavoritesLoaded(true);
      }
    } else {
      setFavoritesLoaded(true);
      setFavorites([]);
    }
  }, [isAuthenticated, user]);

  // Fetch user favorites if authenticated
  useEffect(() => {
    fetchFavorites();
  }, [fetchFavorites, refreshFavorites]);

// Handle favorite toggle
const handleFavoriteToggle = (bookId: string, isFavorite: boolean) => {
  if (isFavorite) {
    // Add to favorites locally first for immediate feedback
    setFavorites(prev => [...prev, bookId]);
  } else {
    // Remove from favorites locally first for immediate feedback
    setFavorites(prev => prev.filter(id => id !== bookId));
  }
  
  // Trigger a refresh of favorites from the server after a short delay
  // This ensures our UI is in sync with the backend state
  setTimeout(() => {
    setRefreshFavorites(prev => prev + 1);
  }, 300);
};

/**
 * BookCard component displays a book in a card format
 * with image, title, author and rating
 */
const BookCard: React.FC<BookCardProps> = ({ book, isFavorite = false, onFavoriteToggle }) => {
  const [favorite, setFavorite] = useState(isFavorite);
  const [isProcessing, setIsProcessing] = useState(false);
  const { isAuthenticated } = useAuth();
  
  // Update internal state when parent props change
  React.useEffect(() => {
    setFavorite(isFavorite);
  }, [isFavorite]);

// Handle favorite toggle
const handleFavoriteToggle = async (e: React.MouseEvent) => {
  e.preventDefault(); // Prevent navigation to book details
  e.stopPropagation(); // Prevent event bubbling
  
  if (!isAuthenticated || isProcessing) return;
  
  try {
    setIsProcessing(true);
    
    const newFavoriteState = !favorite;
    
    if (favorite) {
      await userService.removeFavorite(book.id);
    } else {
      await userService.addFavorite(book.id);
    }
    
    // Update local state immediately for better user experience
    setFavorite(newFavoriteState);
    
    // Notify parent component if callback exists
    if (onFavoriteToggle) {
      onFavoriteToggle(book.id, newFavoriteState);
    }
  } catch (error) {
    console.error('Failed to toggle favorite:', error);
    // Revert to original state if there's an error
    setFavorite(favorite);
  } finally {
    setIsProcessing(false);
  }
};

{isAuthenticated && (
  <button 
